import React from 'react';
import './Footer.scss';
import { Link } from 'react-router-dom';

// Import the TTF font
import researcherFont from '/home/raj/Projects/Crop/src/Fonts/researcher/ttf/researcher.ttf';

// Define a CSS class for the font
const researcherTextStyle = {
  fontFamily: 'Researcher, sans-serif', // Use the imported font
  fontSize: '24px',
  textAlign: 'center',
  fontWeight: 'bold', // Increase font weight to bold
  color: 'white', // Text color
  background: 'rgba(0, 0, 0, 0.5)', // Transparent black background
  padding: '5px 10px', // Add padding for better visibility
  // You can add other CSS styles like margin, etc. here
};

function Footer() {
  return (
    <div className="section footerSection">
      {/* Apply the custom font class to the <p> element */}
      <p style={researcherTextStyle}>
        Team: <span style={{ fontWeight: 'normal', background: 'transparent' }}>Beta Qualifiers</span>
      </p>

      <div className="footerLinksDiv">
        {/* Add your footer links here */}
        <Link to="#section1">Home</Link>
        <Link to="#section2">Try it out</Link>
        <Link to="#section3">About</Link>
      </div>
    </div>
  );
}

export default Footer;
